$(document).ready(function () {
    // ===== SLIDRER =========================
    $('.intro__slider').on('init', function (event, slick) {
        $('.slick-active .slider__item-title').removeClass('visible');
        $('.slick-active .slider__item-title').addClass('animated visible');
        $('.slick-active .slider__item-subtitle').removeClass('hidden');
        $('.slick-active .slider__item-subtitle').addClass('animated visible');
        $('.slick-active .intro__link').removeClass('hidden');
        $('.slick-active .intro__link').addClass('animated visible');
    });

    $('.intro__slider').on('beforeChange', function (event, slick, currentSlide, nextSlide) {
        $('.slick-active .slider__item-title').addClass('hidden');
        $('.slick-active .slider__item-title').removeClass('animated visible');
        $('.slick-active .slider__item-subtitle').addClass('hidden');
        $('.slick-active .slider__item-subtitle').removeClass('animated visible');
        $('.slick-active .intro__link').addClass('hidden');
        $('.slick-active .intro__link').removeClass('animated visible');
    });

    $('.intro__slider').on('afterChange', function (event, slick, currentSlide) {
        $('.slick-active .slider__item-title').removeClass('hidden');
        $('.slick-active .slider__item-title').addClass('animated visible');
        $('.slick-active .slider__item-subtitle').addClass('animated visible');
        $('.slick-active .slider__item-subtitle').removeClass('hidden');
        $('.slick-active .intro__link').addClass('animated visible');

    });


    $(".intro__slider").slick({
        arrows: false,
        dots: true,
        slidesToShow: 1,
        speed: 500,
        autoplay: true,
        autoplaySpeed: 10000,
        infinite: true,
        waitForAnimate: true,
        pauseOnHover: false,
        pauseOnFocus: false,
        waitForAnimate: true,
        fade: true,
        cssEase: "linear",
        // responsive: [
        //     {
        //         breakpoint: 580,
        //         settings: {
        //             arrows: false
        //         }
        //     }
        // ]
    });
    $('.slider').slick('setPosition');
    $(".reviews__slider").slick({
        arrows: false,
        dots: true,
        slidesToShow: 1,
        adaptiveHeight: true,
        speed: 500,
        autoplay: false,
        autoplaySpeed: 10000,
        infinite: true,
        waitForAnimate: true,
        pauseOnHover: false,
        pauseOnFocus: false,
        waitForAnimate: true,
        // responsive: [
        //     {
        //         breakpoint: 580,
        //         settings: {
        //             arrows: false
        //         }
        //     }
        // ]
    });
});
