//  SCROLL ANIMATION ================================================
const animItems = document.querySelectorAll('.anim-items');

if (animItems.length > 0) {
    function animOnScroll(params) {
        window.addEventListener('scroll', animOnScroll); //  Событие
        for (let index = 0; index < animItems.length; index++) {
            const animItem = animItems[index];
            const animItemHeight = animItem.offsetHeight;
            const animItemOffset = offset(animItem).top;
            const animStart = 4;

            let animItemPoint = window.innerHeight - animItemHeight / animStart;

            if (animItemHeight > window.innerHeight) {
                animItemPoint = window.innerHeight - window.innerHeight / animStart;
            }
            if ((pageYOffset > animItemOffset - animItemPoint) && pageYOffset < (animItemOffset + animItemHeight)) {
                animItem.classList.add('anim-active');
            }
            else {
                if (!animItem.classList.contains('anim-no-hide')) { // Если есть клас anim-no-hide - повторной анимации не будет
                    animItem.classList.remove('anim-active'); // Повторная аанимация обьекта
                }
            }
        }
    }
    function offset(el) {
        const rect = el.getBoundingClientRect(),
            scrollLeft = window.pageXOffset || document.documentElement.scrollLeft,
            scrollTop = window.pageYOffset || document.documentElement.scrollTop;
        return { top: rect.top + scrollTop, left: rect.left + scrollLeft }
    }

    setTimeout(function () {
        animOnScroll();
    }, 300);

}

// COUNTER ===================================
let time = 1,
    cc = 1;
$(window).scroll(function () {
    $('#counter').each(function () {
        let cPos = $(this).offset().top,
            topWindow = $(window).scrollTop();
        if (cPos < topWindow + 700) {
            $('.number').addClass('active');
            if (cc < 2) {
                $('div').each(function () {
                    let i = 1,
                        num = $(this).data('num'),
                        step = 1000 * time / num,
                        that = $(this),
                        int = setInterval(function () {
                            if (i <= num) {
                                that.html(i);
                            } else {
                                cc = cc + 2;
                                clearInterval(int);
                            }
                            i++;
                        }, step);
                });
            }
        }
    });
});

// FILTER ================================================
$(function () {
    /*  Filter
    ==============================*/

    let filter = $("[data-filter]");

    filter.on("click", function (event) {
        event.preventDefault();

        let cat = $(this).data('filter');

        if (cat == 'all') {
            $("[data-cat]").removeClass("hide");
        } else {
            $("[data-cat]").each(function () {

                let workCat = $(this).data('cat');

                if (workCat != cat) {
                    $(this).addClass('hide');
                } else {
                    $(this).removeClass('hide');
                }

            });
        }
    });
});

